import React from 'react'
import styled from 'styled-components'

const arrblog = [
    {
        name:" RIGHT SIDEBAR",
        Subchild:0
    },
    {
       name:"LEFT SIDEBAR",
       Subchild:0
   },{
       name:"THREE COLUMNS",
       Subchild:0
   },{
       name:"POST TYPES",
       Subchild:1,
       sub:["STANDARD","GALLERY","LINK","QUETE","VIDEO","AUDIO"]
   }
];

const Child_mb = () =>{

    return(
        <Box_child className="child">
             <li><a href="" className="a_child">MAIN HOME</a></li>    
             <li><a href="" className="a_child">APP PRESENTATION</a></li>    
             <li><a href="" className="a_child">CRYPTO ADVISOR</a></li>    
             <li><a href="" className="a_child">CRYPTO AGENCY</a></li>    
             <li><a href="" className="a_child">ICO HOME</a></li>    
             <li><a href="" className="a_child">BLOCKCHAIN APP</a></li>    
             <li><a href="" className="a_child">VERTICAL SLIDER</a></li>    
             <li><a href="" className="a_child">BLOG HOME</a></li>    
             <li><a href="" className="a_child">LANDING</a></li>    
        </Box_child>
    );
}

const Box_child = styled.ul`
    position: absolute;
    background: #151515;
    border: 1px #303030;
    border-top: 2px solid #aceb2f;
    border-radius: 3px;
    box-sizing: border-box;
    padding: 0;
    top: 0;
    width: 255px;
    padding: 20px 0 0 20px  !important;
    height: auto !important;
    left: 100%;
    background: #151515;
    z-index: 2;
    list-style: none;
    display:none;

    li{
        cursor: pointer;
        font-size: 12px;
        line-height: 40px !important;
        position: relative;

        ::after{
            content: "";
            width: 7px;
            height: 7px;
            background: #aceb2f;
            position: absolute;
            left: 15px;
            top: 50%;
            transform: translateY(-50%);
            border-radius: 50%;
            animation: run_left .5s;
            display:none;
        }

        a.a_child{
            color:#888;
            transition:all .5s;
            display:inline-block;
        }   

        

        :hover{
           
            ::after{
                display:block;
            }
                a.a_child{
                    transform: translateX(40px);
                    color:white;
                }

            }
    }

        
    }

    
`

export default Child_mb
