import React, { useState } from 'react'
import styled from 'styled-components'
 const arrHome = ["MAIN HOME","APP PRESENTATION","CRYPTO ADVISOR","CRYPTO AGENCY","ICO HOME","BLOCKCHAIN APP","VERTICAL SLIDER","BLOG HOME","LANDING"];
 const arrPages = ["ABOUT US","TEAM MEMBER","CONTACT US","GET IN TOUCH","404 ERROR PAGE"];
 const arrCurentcies = ["ICO COUNTDOWN","ICO SINGLE"];

const NavBar=()=>{
    const[child_home,Setchild_home]= useState(arrHome);
    const[child_pages,Setchild_pages]= useState(arrPages);
    const[child_Curent,Setchild_Curent]= useState(arrCurent);

   return(
        <Box_nav>
            <Nav className="container">
                <Menu>
                    <Logo_nav>
                        <img src="images/logo.png" alt="" />
                    </Logo_nav>
                    <Contents_nav>

                        <ul>
                            <li className="active"><a href="">HOME</a>
                                <Child className="child">
                                    {child_home.map((chl,idx)=>(
                                        <li key={idx} className={idx==0?"active":""}><a href="" className="a_child">{chl}</a></li>
                                    ))}
                                </Child>
                            </li>
                            <li><a href="">PAGES</a>
                                 <Child className="child">
                                    {child_pages.map((chl,idx)=>(
                                        <li key={idx} className={idx==0?"active":""}><a href="" className="a_child">{chl}</a></li>
                                    ))}
                                </Child>
                            </li>
                            <li><a href="">CURRENCIES</a>
                                 <Child className="child">
                                    {child_.map((chl,idx)=>(
                                        <li key={idx} className={idx==0?"active":""}><a href="" className="a_child">{chl}</a></li>
                                    ))}
                                </Child>
                            </li>
                            <li><a href="">PORTFOLIO</a></li>
                            <li><a href="">BLOG</a></li>
                            <li><a href="">SHOP</a></li>
                            <li><a href="">ELEMENTS</a></li>
                        </ul>

                        <Btn_menu>
                            <div className="btn">
                                Login
                            </div>
                        </Btn_menu>
                    </Contents_nav>
                </Menu>
            </Nav>
        </Box_nav>
   );
}

const Box_nav= styled.nav`
    padding:0;
    position:absolute;
    top:0;
    left:0;
    right:0;
    z-index:9999999;
`

const Nav = styled.div`
    display:block;
    with:100%;
    padding:0;
`
const Menu = styled.div`
    width:100%;
    height:100px;
    line-height:90px;
    display:flex;
    flex-direction:row;
    flex-grow:1;
`

const Logo_nav = styled.div`
    height: 100%;
    flex-grow:1;

    img{
        width:91px;
        height:27px;
    }
`

const Contents_nav = styled.div`
    height: 100%;
    flex-grow:3;
    display:flex;
    flex-direction:row;

    ul{
    height: 100%;
    flex-grow:4;
    padding: 0;
    list-style: none;
    display: flex;
    flex-direction: row;
    justify-content: space-evenly;
    flex-wrap: wrap;   
    }

   

    li{
        cursor: pointer;
        :hover {
            a{
                color:#aceb2f;
            }

            .child{
                display: block !important;
            }

        }
        position:relative;
    }
    

    li a{
        color:white;
        text-decoration:none;
        font-weight:bold;
        font-family: 'Roboto', sans-serif;
        letter-spacing: 2px;
        transition:all .5s;
    }

    li.active{
        a{
            color:#aceb2f;
        }

        .child{
            a{
                color:#989898;
                display:inline-block;
            }
        }

    }

   
`
const Btn_menu = styled.div`
    height: 100%;
    flex-grow:2;
    .btn{
        width:70%;
        padding:13px 0;
        border:2px solid #aceb2f;
        border-radius: 50px;
        color:white;
    }
`


const Child = styled.ul`
    position: absolute;
    background: #151515;
    border: 1px #303030;
    border-top: 2px solid #aceb2f;
    border-radius: 3px;
    box-sizing: border-box;
    padding: 0;
    text-aglin: left;
    top: 100%;
    width: max-content;
    padding: 20px  80px 20px 20px  !important;
    height:auto !important;
    display:none !important;

    li{
        cursor: pointer;
        position: relative;
        font-size: 14px;
        line-height: 30px;
        position:relative;


        ::before{
            content: "";
            width: 7px;
            height: 7px;
            background: #aceb2f;
            position: absolute;
            left: 15px;
            top: 50%;
            transform: translateY(-50%);
            border-radius: 50%;
            animation: run_left .5s;
            display:none;
        }

        @keyframes run_left{
            from{
                transform: translateX(-20px);
            }
        }

         
        
        &.active::before{
                display:block;
        }

        &.active .a_child{
            transform: translateX(40px);
        }
    

        :hover{
           
            ::before{
                display:block;
            }
                .a_child{
                    transform: translateX(40px);
                }
            }
    }
     
`

export default NavBar
