                    a{
                        color:#fff;
                    }
            }

            li.text{
                cursor: pointer;
                position: relative;
                font-size: 14px;
                line-height: 40px;
                position:relative;

                ::before{
                    content: "";
                    width: 7px;
                    height: 7px;
                    background: #aceb2f;
                    position: absolute;
                    left: 15px;
                    top: 50%;
                    transform: translateY(-50%);
                    border-radius: 50%;
                    animation: run_left .5s;
                    display:none;
                }

                :hover{
           
                    ::before{
                        display:block;
                    }
                        a{
                            transform: translateX(40px);
                            color:white;
                        }
                    }
            }
            }
        }
    }
`

export default NavBar
