import react from 'react'
import styled from 'styled-components'
import Child_mb from './child_menu_mb/Child_mb';


const Nav_Mobi =()=>{
 return(
    <Box_nav className="nav_mb">
        <Nav>
           <div className="logo">
           <a href="#"><img src="images/logo_mb.png" alt="" /></a>
           </div>

           <div className="btn_menu">
               <span></span>
               <span></span>
               <span></span>
           </div>
        </Nav>
        <Nav_menu> 
            <Box_menu>
                <div className="Box_logo">
                    <a href="#"><img src="images/logo.png" alt="" /></a>
                </div>

                <Menu>
                <li className="active"><a href="">HOME</a> <i class="fas fa-arrow-right"></i>
                    <Child_mb cla>
                        
                    </Child_mb>
                </li>

                <li ><a href="">PAGES</a> <i class="fas fa-arrow-right"></i></li>

                <li ><a href="">CURRENCIES</a> <i class="fas fa-arrow-right"></i></li>

                <li><a href="">PORTFOLIO</a> <i class="fas fa-arrow-right"></i></li>

                <li><a href="">BLOG</a> <i class="fas fa-arrow-right"></i></li>

                <li><a href="">SHOP</a> <i class="fas fa-arrow-right"></i></li>

                <li><a href="">ELEMENTS</a> <i class="fas fa-arrow-right"></i></li>
                </Menu>
            </Box_menu>
            <Bg_out></Bg_out>
        </Nav_menu>
        
    </Box_nav>
 );
}

const Box_nav= styled.nav`
    padding:0;
    position:fixed;
    top:0;
    left:0;
    right:0;
    z-index:9999999;
    display:block;
    &.nav_mb{
        @media screen and (min-width:1200px){
          display:none !important;
        }
      }
`

const Nav = styled.div`
    display:block;
    with:100%;
    height:80px;
    line-height:80px;
    text-align: center;
    padding:0;
    position:relative;
    background:#ffffff;
    
    .logo{
        width:100px;
        display: inline-block;
        a{
            img{
                width:100%;
                height:100%;
                object-fix: cover;
            }
        }
    }

    .btn_menu{
        width:40px;
        height:40px;
        position: absolute;
        top:50%;
        left:20px;
        transform:translateY(-50%);
        display: flex;
        flex-direction: column;
        justify-content: space-evenly;

        :hover{

            span{
               
                ::before{
                    background:black;

                }
                
                :nth-child(2n-1)::before{
                    width:50%;
                }
            }
            
        }

        span{
            display:block;
            width:100%;
            position:relative;
            ::before{
                content: "";
                position:absolute;
                width:100%;
                height:1px;
                background:#888;
                top:0;
                left:0;
                transition:all .5s;
            }

            
        }
    }
`

const Nav_menu = styled.div`
    padding:0;
    position:fixed;
    top:0;
    left:0;
    right:0;
    bottom:0;
    display: flex;
    flex-direction: row;
    flex-grow: 1;
`

const Box_menu = styled.div`
    flex-grow: 1;
    background: #151515;

    .Box_logo{
        width:100%;
        text-align:center;
        padding:1.5rem 0;
        a {
            display: inline-block;
            width: 100px;
        }
    }
`

const Menu = styled.ul`
    padding: 0;
    list-style: none;
    
    li.active{
        a{
            color:#ffffff
        }
        ::before{
            background: rgb(172, 235, 47);
        }
    }

    li{
        padding: 5px 0;
        padding-left:20px;
        line-height: 70px;
        position:relative;

        ::before{
            content: "";
            position: absolute;
            bottom: 0;
            left: 0;
            width: 100%;
            height: 1px;
            background: #888;
        }

        :hover{
            a{
                color:#ffffff
            }
            ::before{
                background: rgb(172, 235, 47);
            }

            i{
                right: 10px;
                color:white;
            }
        }

        a{
            color:#888;
            text-decoration:none;
        }

        i{
            position: absolute;
            right: 50px;
            top: 50%;
            transform: translateY(-50%);
            transition: all .5s;
        }
    }
`

const Bg_out = styled.div`
    flex-grow: 3;
    background: gray;
    opacity: .7;
`

export default Nav_Mobi
