                            <div className="btn">
                                <a href="">PURCHASE</a>
                            </div>
                        </div>

                    </Box_right_tk>
                </Content_tk>
            </Box_tk>
        );
    }

    const Box_tk = styled.section`
        width:100%;
        padding:7.5rem 0;
    `

    const Content_tk = styled.div`
        display: flex;
        flex-direction: row;
        align-items: center;
        padding: 0;
    `
    const Box_left_tk = styled.div`
        width:100%;
        height: 500px;
        display:flex;
        flex-direction:row;
        justify-content:center;
        align-items: center;
    `

    const Circle = styled.div`
        width:500px;
        height:500px;
        border: 2px dashed #828282;
        border-radius:50%;
        position:relative;

        .item.active .text-circle{
            display:block;
        }

        .item-circle{
            width:120px;
            height:120px;
            border-radius:50%;
            background:#333;
            line-height:120px;
            text-align: center;
            transform: translate(155%,-55%);
            transition:all .5s;

            .img-hv{
                display: none;
            } 

            :hover {
                background:#aceb2f!important;
                 .img{
                    display:none;
                 }
                .img-hv{
                    display:block;
                } 
            }
            
        }

        .item-circle.it2 {
            transform: translate(300%,-97%);
        }

        .item-circle.it3 {
            transform: translate(365%,-45%);
        }

        .item-circle.it4 {
            transform: translate(304%,0%);
        }

        .item-circle.it5 {
            transform: translate(160%,-40%);
        }

        .item-circle.it6 {
            transform: translate(5%,-205%);
        }

        .item-circle.it7 {
            transform: translate(-51%,-444%);
        }

        .item-circle.it8 {
            transform: translate(14%,-697%);
        }
        

        .text-circle{
            width: 300px;
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            color: white;
            text-align: center;
            display:none;
        }
    `

    const Box_right_tk = styled.div`
        width:100%;

        .text_right{
            width:100%;
            text-align:left;    
            padding-bottom:28px;
            padding-left:20%;
            h1{
                color:white;
                font-weight:600;
                font-family: 'Montserrat', sans-serif;
            }

            h5{
                color: #ffffff;
                font-size: 18px;
                font-style: italic;
                font-weight: 400;
                line-height: 28px;
                margin: 11px 0 0 0;
                
            }

            p{
                display: block;
                margin: 14px 0 28px ;
                font-family: "Open Sans",sans-serif;
                line-height: 26px;
                font-weight: 400;
                color: #888;

                span{
                    color: #d4d4d4;
                }
            }

                .btn{
        
                    a{
                        display:block;
                        text-decoration:none;
                        color:white;
                        font-family: 'Montserrat', sans-serif;
                        font-weight:600;
                        padding:15px 25px;
                        border-radius:50px;
                        border:2px solid #aceb2f;
                        font-size:14px;
                    }
                }

        }

    `

    export default Token
