import React from 'react';
import styled from 'styled-components';

function Button(){
    return(
        <ButtonOutter>
            Learn More
            <ButtonBg></ButtonBg>
        </ButtonOutter>
    );
}

const ButtonOutter = styled.a`
    color: #fff;
    position: relative;
    display: block;
    overflow: hidden;

    width: 100%;
    height: auto;
    max-width: 250px;
    border-radius: 50px;
    margin: 1rem auto;

    font: normal 18px/60px 'proxima-nova', sans-serif;
    text-align: center;
    text-decoration: none;
    border: 2px solid #aceb2f;

    :hover {
        color: #eee;
    }
    :hover span {
        width: 225%;
        height: 562.5px;
    }
    :active {
        background-color: #aceb2f;
    }

`

const ButtonBg = styled.span`
    position: absolute;
    display: block;
    width: 0;
    height: 0;
    border-radius: 80%;
    background-color: #abec2f;
    transition: width 0.4s ease-in-out, height 0.4s ease-in-out;
    transform: translate(-50%, -50%);
    z-index: 0;
`

export default Button;
