import React from 'react';
import Slider from 'react-slick';
import styled from 'styled-components';

function Roadmap() {
    var settings = {
            arrow: true,
            infinite: false,
            speed: 400,
            slidesToShow: 4,
            slidesToScroll: 1
        };

    const state = {
        item:[
            {
                styleDate: {
                    bottom: '32px'
                },
                styleLine: {
                    background: '#aceb2f'
                },
                styleContent: {
                    top: '75px',
                    background:'url(../../images/h1-img-2.png)'
                },
                date: 'Q2 2017',
                title: 'Future is Now',
                content: 'Aliquam lorem ante, dapi bus in, viverra quis, feugi at a, tel lus. Phaselus vive'
            },
            {
                styleDate: {
                    top: '32px'
                },
                styleLine: {
                    background: '#aceb2f',
                    background:'url(../../images/h1-img-2.png)'
                },
                styleContent: {
                    bottom: '75px'
                },
                date: 'Q3 2017',
                title: 'London Office',
                content: 'Aliquam lorem ante, dapi bus in, viverra quis, feugi at a, tel lus. Phaselus vive'
            }
        ]
    }

    return (
        <RoadmapSection>
            <RoadmapContainer>
                <div>
                    <Slider {...settings}>
                        {state.item.map(item => (
                            <div>
                                <RoadmapItemHolder>
                                    <RoadmapDate style={item.styleDate}>{item.date}</RoadmapDate>
                                    <RoadmapLiner>
                                        <Line style={item.styleLine}/>
                                        <Dot/>
                                        <Line style={item.styleLine}/>
                                    </RoadmapLiner>
                                    <RoadmapContent style={item.styleContent}>
                                        <h5>{item.title}</h5>
                                        <Content>{item.content}</Content>
                                    </RoadmapContent>
                                </RoadmapItemHolder>
                            </div>
                        ))}

                        <div>
                            <RoadmapItemHolder className="roadmap-bottom">
                                <RoadmapDate style={{bottom: '32px'}}>Q2 2017</RoadmapDate>
                                <RoadmapLiner>
                                    <Line style={{background: '#aceb2f'}}/>
                                    <Dot/>
                                    <Line style={{background: '#aceb2f'}}/>
                                </RoadmapLiner>
                                <RoadmapContent style={{top: '75px'}}>
                                    <h5>Future is Now</h5>
                                    <Content>Aliquam lorem ante, dapi bus in, viverra quis, feugi at a, tel lus. Phaselus vive</Content>
                                </RoadmapContent>
                            </RoadmapItemHolder>
                        </div>
                        <div>
                            <RoadmapItemHolder className="roadmap-top">
                                <RoadmapDate style={{top: '32px'}}>Q2 2017</RoadmapDate>
                                <RoadmapLiner>
                                    <Line style={{background: '#aceb2f'}}/>
                                    <Dot/>
                                    <Line style={{background: '#aceb2f'}}/>
                                </RoadmapLiner>
                                <RoadmapContent style={{bottom: '75px'}}>
                                    <Content>Aliquam lorem ante, dapi bus in, viverra quis, feugi at a, tel lus. Phaselus vive Aliquam lorem ante, dapi bus in, viverra quis, feugi at a, tel lus. Phaselus vive</Content>
                                </RoadmapContent>

                            </RoadmapItemHolder>
                        </div>
                        <div>
                            <RoadmapItemHolder className="roadmap-bottom">
                                <RoadmapDate style={{bottom: '32px'}}>Q2 2017</RoadmapDate>
                                <RoadmapLiner>
                                    <Line style={{background: '#aceb2f'}}/>
                                    <Dot/>
                                    <Line style={{background: '#aceb2f'}}/>
                                </RoadmapLiner>
                                <RoadmapContent style={{top: '75px', background:'url(../../images/h1-img-2.png)'}}>
                                    <Content>Aliquam lorem ante, dapi bus in, viverra quis, feugi at a, tel lus. Phaselus vive Aliquam lorem ante, dapi bus in, viverra quis, feugi at a, tel lus. Phaselus vive</Content>
                                </RoadmapContent>
                            </RoadmapItemHolder>
                        </div>
                        <div>
                            <h3>4</h3>
                        </div>
                        <div>
                            <h3>5</h3>
                        </div>
                        <div>
                            <h3>6</h3>
                        </div>
                    </Slider>
                </div>
            </RoadmapContainer>

        </RoadmapSection>
    );
}

const RoadmapSection = styled.section`
    color: #fff;

    padding: 25% 10% 3%;

    min-height: 750px;

    .slick-list{
        overflow: visible !important;
    }
`

const RoadmapContainer = styled.div`
`

const RoadmapItemHolder = styled.div`
    position: relative;
`

const RoadmapLiner = styled.div`
    display: flex;
    flex-direction: row;
    overflow: visible;
    height: 20px;
    align-items: center;
    justify-content: center;
`

const Line = styled.div`
    width: 50%;
    height: 3px;
`

const Dot = styled.div`
    width: 20px;
    height: 20px;
    background: #fff;
    border: 5px solid #aceb2f;
    border-radius: 5rem;
`

const RoadmapContent = styled.div`
    position: absolute;
    font-family: sans-serif;
    padding: 25px 35px 27px;
    border-radius: 10px;
    box-sizing: border-box;
    background-position: center;
    background-size: cover;
`

const RoadmapDate = styled.div`
    position: absolute;
    width: 100%;
    text-align: center;
`

const Content = styled.div``

export default Roadmap;
